      {foto.map((data, index) => {
        return (
          <div>

            <div className="foto" key={index.toString()} style={{ background: `url(${data.src.medium})` }}  >
              <div >
                <div onClick={() => {

                  const index = galeria.findIndex((e) => e.id === data.id)

                  galeria[index].liked = !galeria[index].liked
